descriptor.templateCss:
.reset-clock-container {
  padding: 16px;
  background: #fff;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.reset-clock-container h4 {
  margin: 0 0 12px 0;
  font-size: 14px;
  color: #333;
}

.clock-info {
  width: 100%;
  margin-bottom: 12px;
}

.clock-info p {
  margin: 4px 0;
  font-size: 11px;
  color: #666;
}

.reset-button {
  padding: 8px 16px;
  background: #2196f3;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 12px;
  cursor: pointer;
  transition: background 0.3s;
}

.reset-button:hover {
  background: #1976d2;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.deviceTime = new Date();
    self.ctx.$scope.serverTime = new Date();

    // Update server time every second
    var timeInterval = setInterval(function() {
        self.ctx.$scope.serverTime = new Date();
        self.ctx.detectChanges();
    }, 1000);

    self.ctx.$scope.resetClock = function() {
        var devEui = self.ctx.defaultSubscription.entityId.id;

        if (self.ctx.datasources && self.ctx.datasources[0] && self.ctx.datasources[0].entity) {
            var entity = self.ctx.datasources[0].entity;
            if (entity.name && entity.name.startsWith('eui-')) {
                devEui = entity.name.replace('eui-', '');
            }
        }

        // Send clock sync command
        var timestamp = Math.floor(Date.now() / 1000);
        var buffer = new Uint8Array(4);
        buffer[0] = (timestamp >> 24) & 0xFF;
        buffer[1] = (timestamp >> 16) & 0xFF;
        buffer[2] = (timestamp >> 8) & 0xFF;
        buffer[3] = timestamp & 0xFF;

        var base64 = btoa(String.fromCharCode.apply(null, buffer));

        var payload = {
            queueItem: {
                confirmed: true,
                data: base64,
                fPort: 4  // Using fPort 4 for clock sync
            }
        };

        var url = 'http://100.92.66.20:8080/api/devices/' + devEui + '/queue';

        self.ctx.http.post(url, payload, {
            headers: {
                'Content-Type': 'application/json',
                'Grpc-Metadata-Authorization': 'Bearer YOUR_API_KEY_HERE'
            }
        }).subscribe(
            function(response) {
                self.ctx.showSuccessToast('Clock sync command sent');
                self.ctx.$scope.deviceTime = new Date();
            },
            function(error) {
                self.ctx.showErrorToast('Failed to sync clock: ' + error.message);
            }
        );
    };

    self.ctx.$scope.$on('$destroy', function() {
        clearInterval(timeInterval);
    });
};

self.onDataUpdated = function() {
};

self.onDestroy = function() {
};
