descriptor.templateCss:
.timepicker-container {
  padding: 20px;
  background: #fff;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  height: 100%;
}

.timepicker-container h3 {
  margin: 0 0 20px 0;
  font-size: 16px;
  color: #333;
}

.schedule-form {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.form-row {
  display: flex;
  flex-direction: column;
}

.form-row label {
  font-size: 12px;
  color: #666;
  margin-bottom: 4px;
}

.form-row input,
.form-row select {
  padding: 8px;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 14px;
}

.schedule-button {
  margin-top: 12px;
  padding: 10px 20px;
  background: #4caf50;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 14px;
  cursor: pointer;
  transition: background 0.3s;
}

.schedule-button:hover {
  background: #45a049;
}

.schedule-info {
  margin-top: 16px;
  padding: 12px;
  background: #fff3cd;
  border-radius: 4px;
  border-left: 4px solid #ffc107;
}

.info-text {
  margin: 0;
  font-size: 11px;
  color: #856404;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.scheduleValve = '0';
    self.ctx.$scope.startTime = null;
    self.ctx.$scope.duration = 60;
    self.ctx.$scope.repeatMode = 'none';

    self.ctx.$scope.sendSchedule = function() {
        // Get device EUI
        var devEui = self.ctx.defaultSubscription.entityId.id;

        if (self.ctx.datasources && self.ctx.datasources[0] && self.ctx.datasources[0].entity) {
            var entity = self.ctx.datasources[0].entity;
            if (entity.name && entity.name.startsWith('eui-')) {
                devEui = entity.name.replace('eui-', '');
            }
        }

        if (!self.ctx.$scope.startTime) {
            self.ctx.showErrorToast('Please select a start time');
            return;
        }

        // TODO: Customize this payload format based on your device's scheduling protocol
        // This is a placeholder that needs to be updated with your specific format
        var scheduleData = {
            valve: parseInt(self.ctx.$scope.scheduleValve),
            startTime: new Date(self.ctx.$scope.startTime).getTime(),
            duration: parseInt(self.ctx.$scope.duration),
            repeat: self.ctx.$scope.repeatMode
        };

        // Encode to base64 (you'll need to customize this based on your protocol)
        var dataStr = JSON.stringify(scheduleData);
        var base64 = btoa(dataStr);

        var payload = {
            queueItem: {
                confirmed: true,
                data: base64,
                fPort: 3  // Using fPort 3 for scheduling (change if needed)
            }
        };

        var url = 'http://100.92.66.20:8080/api/devices/' + devEui + '/queue';

        self.ctx.http.post(url, payload, {
            headers: {
                'Content-Type': 'application/json',
                'Grpc-Metadata-Authorization': 'Bearer YOUR_API_KEY_HERE'
            }
        }).subscribe(
            function(response) {
                self.ctx.showSuccessToast('Schedule sent successfully');
            },
            function(error) {
                self.ctx.showErrorToast('Failed to send schedule: ' + error.message);
            }
        );
    };
};

self.onDataUpdated = function() {
};

self.onDestroy = function() {
};
